import logo from './logo.svg';
import './App.css';
import { PersonaService} from './service/PersonaService';
import React, {Component} from 'react'; 
import {DataTable} from 'primereact/datatable';
import {Column} from 'primereact/column';
import {Panel} from 'primereact/panel';
import 'primereact/resources/themes/nova-light/theme.css';
import 'primereact/resources/primeract.min.css';
import 'primereact/primeicons.css';

export default class App extends Component{
  constructor(){
    super();
    this.state = {};
    this.personaService = new PersonaService();  



  }  

  componentDidMount(){
    this.personaService.getAll().then(data => this.setState({personas: data}))
  }

  render(){
    return(

    <Panel header="React CRUD App" style={{width:'80%'}}>

      <Datatable value={this.state.personas}>
        <Column field="id" header="ID"></Column>
        <Column field="nombre" header="ID"></Column>
        <Column field="apellido" header="ID"></Column>
        <Column field="direccion" header="ID"></Column>
        <Column field="telefono" header="ID"></Column>
      </Datatable>

    </Panel>


    );
  }

}
